let counter = 0;
let intervalId;

function startTimer() {
    intervalId = setInterval(() => {
        counter++;
        document.getElementById("counter").innerText = counter;
    }, 1000);
}

window.addEventListener("DOMContentLoaded", startTimer);

document.getElementById("plus").addEventListener("click", () => {
    counter++;
    document.getElementById("counter").innerText = counter;
});

document.getElementById("minus").addEventListener("click", () => {
    counter--;
    document.getElementById("counter").innerText = counter;
});

const likesList = document.querySelector(".likes");
const likesCount = {};

document.getElementById("heart").addEventListener("click", () => {
    if (!likesCount[counter]) {
        likesCount[counter] = 1;
        const listItem = document.createElement("li");
        listItem.id = `like-${counter}`;
        listItem.innerText = `${counter} has been liked 1 time`;
        likesList.appendChild(listItem);
    } else {
        likesCount[counter]++;
        const listItem = document.getElementById(`like-${counter}`);
        listItem.innerText = `${counter} has been liked ${likesCount[counter]} times`;
    }
});

const pauseButton = document.getElementById("pause");
let isPaused = false;

pauseButton.addEventListener("click", () => {
    if (isPaused) {
        startTimer();
        pauseButton.innerText = "pause";
        enableButtons();
    } else {
        clearInterval(intervalId);
        pauseButton.innerText = "resume";
        disableButtons();
    }
    isPaused = !isPaused;
});

function disableButtons() {
    document.getElementById("plus").disabled = true;
    document.getElementById("minus").disabled = true;
    document.getElementById("heart").disabled = true;
}

function enableButtons() {
    document.getElementById("plus").disabled = false;
    document.getElementById("minus").disabled = false;
    document.getElementById("heart").disabled = false;
}

document.getElementById("comment-form").addEventListener("submit", (event) => {
    event.preventDefault();
    const commentInput = document.getElementById("comment-input");
    const commentText = commentInput.value;
    if (commentText) {
        const commentList = document.getElementById("list");
        const commentElement = document.createElement("p");
        commentElement.innerText = commentText;
        commentList.appendChild(commentElement);
        commentInput.value = ""; // Clear input field
    }
});
